/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  /* Custom theme options */
  --color-accent: black;
  --color-accent2: #999999;
  --color-shade: #2d3339;

  --inner-max-width: 800px;

  /* Base values */
  --box-shadow: 0px 2px 8px rgba(0, 0, 0, 0.1);
  --border-radius: 2px;
  --border-color: #e0e1e1;
  --body-font: "Merriweather", serif;
  --heading-font: "Inter", sans-serif;
}

/* Root element (use instead of `body`) */
#page {
  font-family: var(--heading-font);
  color: #111;
  line-height: 1.5;
  font-size: 1.125rem;
  background: white;
}

/* Elements */
.section-container {
  max-width: 1150px;
  margin: 0 auto;
  padding: 5rem 2rem;
}

a.link {
  line-height: 1.3;
  border-bottom: 2px solid var(--color-accent);
  transform: translateY(-2px); /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-size: clamp(2rem, 10vw, 3rem);
  line-height: 1.1;
  font-weight: 600;
  letter-spacing: -0.15rem;
}

.button {
  color: white;
  background: var(--color-accent, rebeccapurple);
  border-radius: 0;
  padding: 1px 24px;
  transition: var(--transition, 0.1s box-shadow);
  border: 0; /* reset */
  border-radius: 0.25rem;
  font-size: 1rem;

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

.section-container { /* Defined in Site CSS */
  display: grid;
  gap: 2rem;

  @media (min-width: 800px) {
    grid-template-columns: 3fr 2fr;
    gap: 14rem;
  }
}

.content {
  display: grid;
  place-content: flex-start;
  gap: 1rem;

  .body{
    margin-bottom: .5rem;
  }
  .social-links {
    display: grid;
    gap: 0.75rem;

    a {
      display: flex;
      align-items: center;
      gap: 0.75rem;

      .icon {
        font-size: 1.75rem;
        color: var(--color-accent, rebeccapurple);
        transition: var(--transition, 0.1s transform);
      }

      &:hover .icon {
        transform: scale(1.1);
      }
    }
  }
}

form {
  display: grid;
  gap: 1rem;
  padding: 2rem;
  border: 1px solid var(--border-color, #eee);
  border-radius: var(--border-radius);

  label {
    display: grid;
    gap: 0.25rem;

    span {
      font-size: 0.75rem;
      font-weight: 500;
    }

    input,
    textarea {
      font-size: 1rem;
      padding: 0.5rem;
      outline-color: transparent;
      transition: var(--transition, 0.1s outline-color);
      border: 1.5px solid var(--border-color, #eee);
      border-radius: 4px;

      &:focus {
        outline-color: var(--color-accent, rebeccapurple);
      }
    }
  }

  .button { /* Defined in Site CSS */
    margin-top: 1rem;
  }
}

section {
  display: grid;
  place-items: center;
  max-width: var(--inner-max-width, 900px);
  padding-bottom: 3rem;
}

.heading {
  text-align: center;
  text-wrap: balance;
  margin-bottom: 3rem;
}

figure {
  display: grid;
  gap: 0.25rem;
  margin-bottom: 2.5rem;
  img {
    width: 200px;
    height: 200px;
    object-fit: cover;
    border-radius: 100%;
  }
  figcaption {
    color: var(--color-shade, #2d3339);
    font-size: 0.875rem;
    font-weight: 400;
    text-align: center;

  }
}
.content {
    line-height: 1.5;
    font-family: var(--body-font);
    font-weight: 300;
    color: #2d3339;

  :global(p){
    margin-bottom: 1rem;
  }

}

.section-container {
  max-width: var(--inner-max-width, 900px);
}

h2 {
  font-size: 1rem;
  line-height: 1.1;
  font-weight: 600;
  margin-bottom: 4rem;
  letter-spacing: -0.02rem;
  padding: .5rem 0;
  border-bottom: 1px solid #f0f0f0;
}

ul.items {
  display: grid;
  gap: 4rem;
}

li {
  display: grid;
  gap: 2rem;

  .post-info {
    .title {
      display: block;
      font-size: 1.3rem;
      font-weight: 600;
      margin-bottom: 0.75rem;
      line-height: 1.2;
      letter-spacing: -0.02rem;
      text-decoration: underline transparent;
      transition: 0.1s text-decoration;

      &:hover {
        text-decoration-color: initial;
      }
    }

    .description {
      font-size: 1rem;
      margin-bottom: 0.5rem;
      font-family: var(--body-font);
      line-height: 1.4;
      font-weight: 300;
      color: #2d3339;
    }

    .date {
      font-size: 1rem;
      font-weight: 400;
      color: var(--color-accent2);
      font-weight: 400;
    }
  }

  img {
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

@media (min-width: 600px) {
  li {
    grid-template-columns: 3fr 1fr;
    gap: 3rem;
  }
}

