  justify-content: space-between;
  align-items: center;
  padding-block: 2rem;
}

.logo {
  font-size: 1.5rem;
  font-weight: 600;
  line-height: 1.2;
  width: 50px; /* adjust as needed */
  height: 50px;
}

.desktop-nav {
  display: none;
  align-items: center;
  justify-content: space-between;
  width: 100%;

  nav {
    display: flex;
    gap: 1.75rem;

    a.link {
      /* Defined in Site CSS */
      border-color: transparent;
      transition: border-color 0.1s;
      font-size: 0.95rem;
      font-weight: 500;


      &:hover, .active {
        border-color: initial;
      }
    }
  }

  @media (min-width: 800px) {
    display: flex;
  }
}

.mobile-nav {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  font-weight: 500;

  @media (min-width: 800px) {
    display: none;
  }
}

/* Mobile navigation popup */
#popup {
  font-size: 1.25rem;
  display: grid;
  gap: 1rem;
  position: absolute;
  text-align: center;
  background-color: var(--background-color, white);
  box-shadow: var(--box-shadow, 0px 4px 30px rgba(0, 0, 0, 0.1));
  border-radius: var(--border-radius);
  inset: 0.5rem;
  bottom: initial;
  padding: 2rem;
  z-index: 1;

  button#close {
    position: absolute;
    right: 1rem;
    top: 1rem;
  }
}

